
public class MulTable {

	/**
	 * @param args
	 */
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
	
		for (int x=1;x <= 10;x++)
		{
			System.out.println("Multiplication Table for "+x);
			for (int y=1;y < 5;y++)
			{
				System.out.println(x + " X "+y+" = "+ x*y);
			}
		}
			
	}

}
