package com.vinu.learn;

public class BitWise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=2,y=10;
		
		System.out.println("Bitwise AND result is "+(x&y));
		System.out.println("Bitwise OR  result is "+(x|y));
		System.out.println("Bitwise NOT result is "+(~y));
		System.out.println("Bitwise XOR result is "+(x^y));
		
	}

}
